contentCss:
body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  line-height: 1.4;
  margin: 1rem;
}

table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}

figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #e8e8e8;
  border-radius: 3px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

initConfig:
tinymce.init({
  plugins: [
    "a11ychecker advcode casechange formatpainter",
    "linkchecker autolink lists checklist",
    "media mediaembed pageembed permanentpen",
    "powerpaste table advtable tinymcespellchecker"
  ],

	toolbar_mode: 'sliding',
	menubar: false,


  toolbar: "formatselect | bold italic underline strikethrough | forecolor backcolor formatpainter | alignleft aligncenter alignright alignjustify | numlist bullist outdent indent | fontselect | link insertfile image | removeformat | code | addcomment | checklist | casechange",
  height: 360,



});

lessVariables:
@background-color: #fff;
@toolbar-background-color: @background-color;
@menubar-background-color: @background-color;
@border-color: rgba(227, 233, 241, 1);
@color-black: rgba(0, 0, 0, 1);
@color-white: #fff;
@color-tint: rgba(0, 111, 191, 1);
@color-error: rgba(205, 32, 38, 1);
@color-success: rgba(70, 166, 97, 1);
@base-value: 16px;
@control-border-radius: 5px;
@panel-border-radius: 5px;
@tinymce-border-radius: 5px;
@font-stack: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Oxygen-Sans,Ubuntu,Cantarell,"Helvetica Neue",sans-serif;
@font-weight-normal: normal;
@font-weight-bold: bold;
@toolbar-button-icon-color: rgba(73, 76, 78, 1);
@toolbar-button-text-color: rgba(73, 76, 78, 1);
@toolbar-button-hover-background-color: rgba(232, 248, 255, 1);
@toolbar-button-hover-icon-color: rgba(0, 111, 191, 1);
@toolbar-button-hover-text-color: rgba(0, 111, 191, 1);
@toolbar-button-focus-background-color: @toolbar-button-hover-background-color;
@toolbar-button-focus-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-focus-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-background-color: rgba(215, 245, 255, 1);
@toolbar-button-enabled-background-color: rgba(215, 245, 255, 1);
@toolbar-button-active-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-text-color: @toolbar-button-hover-icon-color;
